import React, { useState, useEffect } from "react";
import Work from "../Components/Work";
import { eduDetails, personalDetails, workDetails } from "../Details";

function About() {
  const [fetchedEduDetails, setFetchedEduDetails] = useState([eduDetails,personalDetails]);

  useEffect(() => {
    fetch('http://localhost:3000/show_edu_details')
      .then(response => response.json())
      .then(data => {
        setFetchedEduDetails(data);
        console.log(data); // Log the fetched data
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);

  return (
    <main className="container mx-auto max-width pt-10 pb-20">
      <section>
        <h1 className="text-2xl text-dark-heading dark:text-light-heading md:text-4xl xl:text-5xl xl:leading-tight font-bold">
          About Me
        </h1>
        <p className="text-content py-8 lg:max-w-3xl">{fetchedEduDetails.About}</p>
      </section>

      <section>
        <h1 className="text-2xl pt-10 text-dark-heading dark:text-light-heading md:text-4xl xl:text-5xl xl:leading-tight font-bold">
          Education
        </h1> 
        {fetchedEduDetails.map((item)=>{
          return (
        <div className="bg-gray-100 p-4 rounded-md shadow-md">
            <p className="font-bold">Name: {fetchedEduDetails.name}</p>
            <p>Location: {fetchedEduDetails.Location}</p>
            <p>Education: {fetchedEduDetails.edu}</p>
            <p>Type: {fetchedEduDetails.Type}</p>
            <p>Duration: {fetchedEduDetails.Duration}</p>
          </div>

        )})}
</section>
    </main>
  );
}

export default About;
